"use strict";

new Swiper(".reviews-slider", {
  direction: "vertical",
  loop: !0
}), function (e) {
  var n = e.querySelector("#countdown .display"),
      t = parseInt(n.innerHTML),
      o = setInterval(function () {
    --t >= 0 ? n.innerHTML = t : (e.querySelector("#countdown h1").style.display = "none", e.querySelector("#countdown h2").style.display = "block", clearInterval(o));
  }, 6e4);
}(document);
var hiddenElement = document.getElementById("form"),
    btn = document.querySelectorAll(".btn");

function handleButtonClick() {
  hiddenElement.scrollIntoView({
    block: "center",
    behavior: "smooth"
  });
}

btn.forEach(function (e) {
  e.addEventListener("click", handleButtonClick);
});